      function opensidebar() {
  var element = $("#smart-panel-holder");
  
  // Odd sınıflarını sil
  element.removeClass("count-odd-animation count-blink-odd");
  
  // Even sınıflarını ekle
  element.addClass("count-blink-even");
  
  // "smart-panel-is-visible" sınıfını eklerken durumu tersine çevir
  $("#root").toggleClass("smart-panel-is-visible");
}
function openmenu() {
  var element = $("#bottommenu");
  
  // Odd sınıflarını sil
  element.toggleClass("bottommenu");
}
function closemenu() {
  var element = $("#bottommenu");
  
  // Odd sınıflarını sil
  element.addClass("bottommenu");
}
function openmodal(id){
  closemodal();

  $("#"+id).removeClass("hidden");
  if (id === "profile") {

    // hdr-user-bc class'ına ve smart-panel-holder ID'sine hidden class'ını ekleyin
    $(".hdr-user-bc").toggleClass("hidden");
    $("#smart-panel-holder").toggleClass("hidden");
    $("#profileclosed").removeClass("hidden");
    $("#openaa").removeClass("hidden");
    $("#closeaa").addClass("hidden");


  }
}
function closemodal(id){
  $(".modal").addClass("hidden");
  if (id === "profile") {

// hdr-user-bc class'ına ve smart-panel-holder ID'sine hidden class'ını ekleyin
$(".hdr-user-bc").removeClass("hidden");
$("#smart-panel-holder").removeClass("hidden");
$("#profileclosed").addClass("hidden");
$("#openaa").addClass("hidden");
    $("#closeaa").removeClass("hidden");
}
}
function backModal(id){
  if (id === "notifications" || id === "favourites"|| id === "settings") {
  closemodal();
} 
else{
  closemodal();
  openmodal('profile');
}
}
function openaccordion(id){
  closeaccordion();

  $("#"+id).removeClass("hidden");
}
function closeaccordion(){
  $(".menu").addClass("hidden");
}
function backaccordion(){
  closeaccordion();
  openaccordion('mainmenu');
}
function openbetslip(id){
  closebetslip();

  $("#"+id).removeClass("hidden");
}
function closebetslip(){
  $(".betslips").addClass("hidden");
}
function openfootermenu(id){

$("#"+id).toggleClass("active");

}
$(document).ready(function() {
  var $carouselSlide = $('.carousel-slide');
  var $carouselCount = $('.carousel-count');
  var currentIndex = 0; // Başlangıç indeksi 0 olarak ayarlandı

  // Slick Slider'ı başlatırken
  $('.slider-slickone').slick({
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,
    dots: false,
    infinite: true,
    slidesToShow: 1,
    slidesToScroll: 1,
    swipeToSlide: true
  });

  // Slayt değiştiğinde
  $('.slider-slickone').on('afterChange', function(event, slick, currentSlide) {
    currentIndex = currentSlide;
    updateSlideCount();
  });

  // Slayt sayısını günceller
  function updateSlideCount() {
    var currentNumber = currentIndex + 1;
    var totalCount = $carouselSlide.length -2;
    $carouselCount.text(currentNumber + ' / ' + totalCount);
  }

  updateSlideCount(); // Başlangıçta slayt sayısını güncelle
});

$(document).ready(function() {
    $('.form-control-input-bc').on('focus', function() {
        $(this).closest('.form-control-bc').addClass('focused');
    });

    $('.form-control-input-bc').on('blur', function() {
        var inputValue = $(this).val();
        if (inputValue === '') {
            $(this).closest('.form-control-bc').removeClass('focused');
        }
    });

    // Sayfa yüklendiğinde mevcut değere göre sınıfı ayarla
    $('.form-control-input-bc').each(function() {
        var inputValue = $(this).val();
        if (inputValue !== '') {
            $(this).closest('.form-control-bc').addClass('focused');
        }
    });
});


</script>