      function opensidebar() {
  var element = $("#smart-panel-holder");
  
  // Odd sınıflarını sil
  element.removeClass("count-odd-animation count-blink-odd");
  
  // Even sınıflarını ekle
  element.addClass("count-blink-even");
  
  // "smart-panel-is-visible" sınıfını eklerken durumu tersine çevir
  $("#root").toggleClass("smart-panel-is-visible");
}
function openmenu() {
  var element = $("#bottommenu");
  
  // Odd sınıflarını sil
  element.toggleClass("bottommenu");
}
function closemenu() {
  var element = $("#bottommenu");
  
  // Odd sınıflarını sil
  element.addClass("bottommenu");
}
function openmodal(id){
  closemodal();

  $("#"+id).removeClass("hidden");
  if (id === "profile") {

    // hdr-user-bc class'ına ve smart-panel-holder ID'sine hidden class'ını ekleyin
    $(".hdr-user-bc").toggleClass("hidden");
    $("#smart-panel-holder").toggleClass("hidden");
    $("#profileclosed").removeClass("hidden");
    $("#openaa").removeClass("hidden");
    $("#closeaa").addClass("hidden");


  }
}
function closemodal(id){
  $(".modal").addClass("hidden");
  if (id === "profile") {

// hdr-user-bc class'ına ve smart-panel-holder ID'sine hidden class'ını ekleyin
$(".hdr-user-bc").removeClass("hidden");
$("#smart-panel-holder").removeClass("hidden");
$("#profileclosed").addClass("hidden");
$("#openaa").addClass("hidden");
    $("#closeaa").removeClass("hidden");
}
}
function backModal(id){
  if (id === "notifications" || id === "favourites"|| id === "settings") {
  closemodal();
} 
else{
  closemodal();
  openmodal('profile');
}
}
function openaccordion(id){
  closeaccordion();

  $("#"+id).removeClass("hidden");
}
function closeaccordion(){
  $(".menu").addClass("hidden");
}
function backaccordion(){
  closeaccordion();
  openaccordion('mainmenu');
}
