<div ng-view="" ng-class="$root.currentPage.path.split('/').join('')" class="view-container">
        
<style type="text/css">
.new-default-popup-j {
  z-index: 999999!important;
}  
</style>

<div class="new-default-popup-j" ng-escape="env.showSlider=false; env.sliderContent='';">
