            <i class="bonuses-icon-tab-b"></i>
            <h4 trans="">Bonuslar</h4>
          </li>
        </ul>
        <div ng-show="!isPopup" ng-click="env.sliderContent=''; env.showSlider=false;" class="my-bets-closed-icon-b" onclick="window.location='./';"></div>
